CREATE DATABASE IF NOT EXISTS `bd2_ex10` COLLATE utf8_unicode_ci;
use bd2_ex10;

DROP TABLE IF EXISTS `houblon`;

CREATE TABLE `houblon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(50) DEFAULT NULL,
  `pays` varchar(50) DEFAULT NULL,
  `utilisation` varchar(50) DEFAULT NULL,
  `alpha` varchar(50) DEFAULT NULL,
  `beta` varchar(50) DEFAULT NULL,
  `coh` varchar(50) DEFAULT NULL,
  `huile` varchar(50) DEFAULT NULL,
  `profil` varchar(255) DEFAULT NULL,
  `similitude` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;







